"use client";

import React, { forwardRef, useRef } from "react";

import { cn } from "@/lib/utils";
import { AnimatedBeam } from "../ui/animated-beam";

const Circle = forwardRef<
  HTMLDivElement,
  { className?: string; children?: React.ReactNode }
>(({ className, children }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "z-10 flex size-12 items-center justify-center rounded-full border-2 border-border bg-slate-200 p-3 shadow-[0_0_20px_-12px_rgba(0,0,0,0.8)]",
        className
      )}
    >
      {children}
    </div>
  );
});

Circle.displayName = "Circle";

export function ArchitectureBeamAnimation({
  className,
}: {
  className?: string;
}) {
  const containerRef = useRef<HTMLDivElement>(null);
  const div1Ref = useRef<HTMLDivElement>(null);
  const div2Ref = useRef<HTMLDivElement>(null);
  const div3Ref = useRef<HTMLDivElement>(null);
  const div4Ref = useRef<HTMLDivElement>(null);
  const div5Ref = useRef<HTMLDivElement>(null);
  const div6Ref = useRef<HTMLDivElement>(null);
  const div7Ref = useRef<HTMLDivElement>(null);

  return (
    <div
      className={cn(
        "relative flex h-full w-full items-center justify-center",
        className
      )}
      ref={containerRef}
    >
      <div className="flex size-full flex-row items-stretch justify-between lg:gap-8 md:gap-14 gap-2 lg:max-w-3xl max-w-full">
        <div className="flex flex-col justify-center items-center gap-16 ">
          <div className="flex flex-row items-start justify-between lg:gap-16 md:gap-12 gap-4">
            <span className="flex flex-col gap-1 items-center">
              <Circle ref={div1Ref} className="lg:size-16">
                <Icons.googleDrive />
              </Circle>
              <p className="text-xs text-white font-secondary font-semibold lg:text-nowrap text-center">
                resume (input)
              </p>
            </span>
            <span className="flex flex-col gap-1 items-center w-fit">
              <Circle ref={div2Ref}>
                <Icons.llamaparser />
              </Circle>
              <p className="text-xs text-white font-secondary font-semibold lg:text-nowrap text-center">
                doc parseing
              </p>
            </span>
          </div>
          <span className="flex flex-col gap-1 items-center w-min">
            <Circle ref={div3Ref} className="lg:size-16">
              <Icons.googleDocs />
            </Circle>
            <p className="text-xs text-white font-secondary lg:text-nowrap text-center font-semibold">
              job desc (input)
            </p>
          </span>
        </div>
        <div className=" flex flex-col gap-36 justify-between items-center ">
          <span className="flex flex-col gap-1 items-center w-min">
            <Circle ref={div6Ref}>
              <Icons.huggingface />
            </Circle>
            <p className="text-xs text-white font-secondary font-semibold lg:text-nowrap text-center">
              hugging face
            </p>
          </span>

          <span className="flex flex-col gap-1 items-center w-min">
            <Circle ref={div7Ref}>
              <Icons.customPrompt />
            </Circle>
            <p className="text-xs text-white font-secondary font-semibold lg:text-nowrap text-center">
              custom prompt
            </p>
          </span>
        </div>
        <div className="flex flex-col justify-center items-center">
          <span className="flex flex-col gap-1 items-center w-min">
            <Circle ref={div4Ref} className="lg:size-16">
              <Icons.metallama />
            </Circle>
            <p className="text-xs text-white font-secondary font-semibold lg:text-nowrap text-center">
              llama 3.3
            </p>
          </span>
        </div>
        <div className="flex flex-col justify-center">
          <span className="flex flex-col gap-1 items-center w-min">
            <Circle ref={div5Ref}>
              <Icons.user />
            </Circle>
            <p className="text-xs text-white font-secondary font-semibold lg:text-nowrap text-center">
              user
            </p>
          </span>
        </div>
      </div>

      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div1Ref}
        toRef={div2Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div2Ref}
        toRef={div4Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div3Ref}
        toRef={div4Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div4Ref}
        toRef={div5Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div6Ref}
        toRef={div4Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div7Ref}
        toRef={div4Ref}
      />
    </div>
  );
}

